function InputSample() {
    const [text, setText] = useState('');

    const onChange = (e) => {
        setText(e.target.value);
    }

    const onClick = (e) => {
        setText('');


    }

  return (
    <div>
      <input type='text' onChange={onChange} value={text}/>
      <button onClick={onClick}>초기화</button>
      <div>
        <p>값: {text}</p>
      </div>
    </div>
  );
}

export default InputSample;
