import {useState, useRef, useEffect} from 'react';
import ReviewForm from "./ReviewForm";
import ReviewList from "./ReviewList";
import './App.css';

function App() {
const [data, setData] = useState([]);
const idRef = useRef(0)
const getData = async() => {
    const res = fetch('https://jsonplaceholder.typicode.com/comments').then(res => res.json())
    console.log(res)
}
const initData = res.slice(0, 20).map(list => {
    return {
        title: list.email,
        content: list.body,
        emotion: Math.floor(Math.random() * 5) + 1,
        create_date: new Date().getTime() + 1,
        id: idRef.current++  
    }
    setData(initData)
})

useEffect(() => {
    getData();
}, []);

const onCreate = (title, content, emotion) => {
  const create_date=new Date().getTime()
  const newItem={
    title,
    content,
    emotion,
    create_date,
    id:idRef.current
  }
  idRef.current += 1
  setData([newItem,...data])
}

const onRemove = (targetId) => {
  const newReviewList = data.filter((item) => item.id !== targetId)
  setData(newReviewList)
}

const onEdit = (targetId,newContent) => {
 setData(data.map(list => list.id === targetId ? {...list,content:newContent} : list))
}
  return (
    <div className = "App">
    <ReviewForm onCreate={onCreate}/>
    <ReviewList reviewList = {data} onRemove={onRemove} onEdit = {onEdit}/>
    </div>
  );
}

export default App;
